create table medicine(drug_number varchar(255) not null ,approval_number varchar(255)  ,product_name varchar(255) ,english_name varchar(255)  ,goods_name varchar(255) ,dosage_form varchar(255) ,specification varchar(255) ,production_unit varchar(255)  ,production_address varchar(255) ,product_category varchar(255) ,approval_date varchar(255)  ,original_approval_number date ,drug_code date ,drug_code_note varchar(255) ,primary key(id));