create table medicine_company(drug_company_number varchar(255) not null ,number varchar(255)  ,organization_code varchar(255) ,classification_code varchar(255) ,provinces_cities varchar(255) ,company_name varchar(255) ,legal_representative varchar(255)  ,CEO varchar(255)  ,quality_leader varchar(255) ,registered_address varchar(255)  ,production_address varchar(255)  ,production_range varchar(255) ,issue_date date  ,valid_until date  ,issuing_authority varchar(255)  ,signer varchar(255) ,daily_regulatory_agency varchar(255)  ,daily_supervisors varchar(255)  ,supervision_number varchar(255) ,primary key(id));